api = Blueprint('api', __name__, url_prefix='/api')

@api.route('/categories', methods=('GET',))
def get_categories():
    debate = request.values.get('debate')
    if not debate:
        return jsonify(data={'msg': 'Debate is a required field'}), 400

    return jsonify(data=query_categories(debate))

@api.route('/questions', methods=('GET',))
def get_questions():
    category = request.values.get('category')
    debate = request.values.get('debate')

    if not debate:
        return jsonify(data={'msg': 'Debate is a required field'}), 400

    return jsonify(data=query_questions(debate, category))

@api.route('/debates', methods=('GET',))
def get_debates():
    return jsonify(data=query_debates())

@api.route('/bulkUpload', methods=('POST',))
def upload_questions():
    body = request.json['rawHtml']
    title = request.json['debate']

    try:
        questions = create_parsed_comment_list(body)
        result = upload_questions_query(title, questions)
    except:
        return jsonify(data={'msg': 'Something went wrong, please try again'}), 400
    return jsonify(data={'msg': 'Everthing uploaded successfully'})

